import React, { Component } from "react";
import Footer from "./Footer";
import Card from"./Card";
import axios from "axios"
export class Projects extends Component {
constructor(props) {
  super(props)

  this.state = {
     project_list:[]
  }
}

 updateList =async() =>{

  try{
    axios.get('http://127.0.0.1:8000/projects/').then((res)=>{
      console.log("response database",res.data.results)
      this.setState({
        project_list:res.data.results
      })
    }).catch(err=>{
      console.log("err", err)
    })

    console.log(this.state.project_list)
  }
  catch(err){
    console.log("err", err)
  }


}


componentDidMount(){

this.updateList()
return true
}


  render() {

    return (
      <div>

        <div className="projectPageMainDiv">
       { this.state.project_list.map((item, index)=>{
         console.log("source", item.image)
         return  (
          <a href='#'>
         <Card key={index+1} name={item.name} src={item.image} description={item.description}/>
         </a>)
       })} 
        </div>
        <Footer />
      </div>
    );
  }
}

export default Projects;
