import React, { Component } from "react";
import Footer from "./Footer";
import { DiJavascript1 } from "react-icons/di";
import {
  FaHtml5,
  FaCss3,
  FaNodeJs,
  FaReact,
  FaJava,
  FaPython,
} from "react-icons/fa";
import axios from 'axios'
export class About extends Component {
  constructor(props) {
    super(props)

    this.state = {
       subject:null,
       email:null,
       name:null,
       message:null,
       about_list:[],
       program_list:[]
    }
  }
    onChange=(event)=>{
      event.preventDefault()
      this.setState({
        ...this.state,
        [event.target.name]: event.target.value
      })
    }
    onHandleSubmit =(event)=>{
      const {email,subject, name,message}=this.state
      event.preventDefault()

     const config ={
        headers:{
          'Content-Type':'application/json'
        }

      }
     const body= JSON.stringify({ 'email':email,'subject': subject,'name':name,'message':message })
     console.log(body)
