import React, { Component } from "react";
import Footer from "./Footer";
import Card from"./Card";
import axios from "axios"
export class Projects extends Component {
constructor(props) {
  super(props)

  this.state = {
     project_list:[]
  }
}

 updateList =async() =>{

  try{
    const res=axios.get('http://127.0.0.1:8000/projects/');
  this.setState({
    project_list:res.result
  })
  console.log("Data from data base",res.result)
  }
  catch(err){
    console.log("err", err)
  }


}


componentDidMount(){
  console.log("yeni bir data akimi")
this.updateList()
return true
}


  render() {

    return (
      <div>
        <div className="projectPageMainDiv">
       { this.state.project_list.map((item, index)=>{
         return  (<Card name={item.name} image={`http://127.0.0.1:8000${item.image}`} description={item.description}/>)
       })

  }
        </div>
        <Footer />
      </div>
    );
  }
}

export default Projects;
